function setupTabs() {
    const tabButtons = document.querySelectorAll('.tab-btn');
    const tabContents = document.querySelectorAll('.tab-content');

    tabButtons.forEach(button => {
        button.addEventListener('click', function() {
            const targetTab = this.getAttribute('data-tab');

            // Remove active class from all buttons and contents
            tabButtons.forEach(btn => btn.classList.remove('active'));
            tabContents.forEach(content => content.classList.remove('active'));

            // Add active class to clicked button and corresponding content
            this.classList.add('active');
            const targetContent = document.getElementById(targetTab + '-tab');
            if (targetContent) {
                targetContent.classList.add('active');
            }

            // Update content header title based on tab
            const contentHeader = document.querySelector('.content-header h2');
            if (contentHeader) {
                switch(targetTab) {
                    case 'projects':
                        contentHeader.textContent = 'Projects';
                        break;
                    case 'collaboration':
                        contentHeader.textContent = 'Collaboration';
                        break;
                    case 'donation':
                        contentHeader.textContent = 'Donation';
                        break;
                    case 'history':
                        contentHeader.textContent = 'History';
                        break;
                    default:
                        contentHeader.textContent = this.textContent;
                }
            }

            // Clear any existing search notifications
            const existingNotification = document.querySelector('.search-notification');
            if (existingNotification) {
                existingNotification.remove();
            }

            // Update search placeholder for the new tab
            updateSearchPlaceholder();

            // Load data for the selected tab - only load if content is empty
            switch(targetTab) {
                case 'projects':
                    const projectsGrid = document.getElementById('user-projects-grid');
                    if (!projectsGrid || projectsGrid.children.length === 0) {
                        loadUserProjects();
                    }
                    break;
                case 'collaboration':
                    const collaborationsList = document.getElementById('user-collaborations-list');
                    if (!collaborationsList || collaborationsList.children.length === 0) {
                        loadUserCollaborations();
                    }
                    break;
                case 'donation':
                    const donationsList = document.getElementById('user-donations-list');
                    if (!donationsList || donationsList.children.length === 0) {
                        loadUserDonations();
                    }
                    break;
                case 'history':
                    const activityFeed = document.getElementById('user-activity-feed');
                    if (!activityFeed || activityFeed.children.length === 0) {
                        loadUserActivity();
                    }
                    break;
            }
        });
    });
}

function setupModals() {
    const editProfileBtn = document.getElementById('edit-profile-btn');
    const saveProfileBtn = document.getElementById('save-profile-btn');
    const closeModalBtns = document.querySelectorAll('.close-modal');

    if (editProfileBtn) {
        editProfileBtn.addEventListener('click', openEditProfileModal);
    }

    if (saveProfileBtn) {
        saveProfileBtn.addEventListener('click', handleSaveProfile);
    }

    closeModalBtns.forEach(btn => {
        btn.addEventListener('click', closeModals);
    });

    // Close modal when clicking outside
    document.addEventListener('click', function(e) {
        if (e.target.classList.contains('modal')) {
            closeModals();
        }
    });
}

function setupProfileImage() {
    const profileAvatar = document.getElementById('profile-avatar-container');
    const imageInput = document.getElementById('profile-image-input');
    const removeImageBtn = document.getElementById('remove-image-btn');

    if (profileAvatar && !viewingUserId) {
        profileAvatar.addEventListener('click', () => {
            if (imageInput) imageInput.click();
        });
    }

    if (imageInput) {
        imageInput.addEventListener('change', handleImageSelect);
    }

    if (removeImageBtn) {
        removeImageBtn.addEventListener('click', handleImageRemove);
    }
}

function handleImageSelect(event) {
    const file = event.target.files[0];
    if (!file) return;

    // Validate file type
    if (!file.type.startsWith('image/')) {
        alert('Please select an image file.');
        return;
    }

    // Validate file size (max 2MB)
    if (file.size > 2 * 1024 * 1024) {
        alert('Image size should be less than 2MB.');
        return;
    }

    const reader = new FileReader();
    reader.onload = function(e) {
        const base64Image = e.target.result;

        // Update preview in modal
        const previewImg = document.getElementById('edit-preview-img');
        const defaultPreview = document.getElementById('edit-default-preview');
        const removeBtn = document.getElementById('remove-image-btn');

        if (previewImg && defaultPreview && removeBtn) {
            previewImg.src = base64Image;
            previewImg.style.display = 'block';
            defaultPreview.style.display = 'none';
            removeBtn.style.display = 'inline-flex';
        }

        // Store the base64 image for saving
        document.getElementById('edit-image-preview').dataset.imageData = base64Image;
    };
    reader.readAsDataURL(file);
}

function handleImageRemove() {
    const previewImg = document.getElementById('edit-preview-img');
    const defaultPreview = document.getElementById('edit-default-preview');
    const removeBtn = document.getElementById('remove-image-btn');
    const imageInput = document.getElementById('profile-image-input');

    if (previewImg && defaultPreview && removeBtn && imageInput) {
        previewImg.style.display = 'none';
        previewImg.src = '';
        defaultPreview.style.display = 'flex';
        removeBtn.style.display = 'none';
        imageInput.value = '';

        // Mark for removal
        document.getElementById('edit-image-preview').dataset.imageData = 'REMOVE';
    }
}

function openEditProfileModal() {
    if (!currentUser) return;

    // Pre-fill form with current user data
    document.getElementById('edit-full-name').value = currentUser.full_name || '';
    document.getElementById('edit-email').value = currentUser.email || '';
    document.getElementById('edit-college').value = currentUser.college || '';
    document.getElementById('edit-bio').value = currentUser.bio || '';
    document.getElementById('edit-skills').value = currentUser.skills || '';
    document.getElementById('edit-phone').value = currentUser.phone || '';
    document.getElementById('edit-location').value = currentUser.location || '';
    document.getElementById('edit-title').value = currentUser.title || '';
    document.getElementById('edit-twitter').value = currentUser.twitter || '';
    document.getElementById('edit-linkedin').value = currentUser.linkedin || '';
    document.getElementById('edit-github').value = currentUser.github || '';

    // Set up image preview
    const previewImg = document.getElementById('edit-preview-img');
    const defaultPreview = document.getElementById('edit-default-preview');
    const removeBtn = document.getElementById('remove-image-btn');

    if (currentUser.profile_image) {
        previewImg.src = currentUser.profile_image;
        previewImg.style.display = 'block';
        defaultPreview.style.display = 'none';
        removeBtn.style.display = 'inline-flex';
    } else {
        previewImg.style.display = 'none';
        defaultPreview.style.display = 'flex';
        removeBtn.style.display = 'none';
    }

    const modal = document.getElementById('edit-profile-modal');
    modal.classList.add('show');
}

async function handleSaveProfile() {
    const fullName = document.getElementById('edit-full-name').value;
    const email = document.getElementById('edit-email').value;
    const college = document.getElementById('edit-college').value;
    const bio = document.getElementById('edit-bio').value;
    const skills = document.getElementById('edit-skills').value;
    const phone = document.getElementById('edit-phone').value;
    const location = document.getElementById('edit-location').value;
    const title = document.getElementById('edit-title').value;
    const twitter = document.getElementById('edit-twitter').value;
    const linkedin = document.getElementById('edit-linkedin').value;
    const github = document.getElementById('edit-github').value;

    if (!fullName.trim() || !email.trim() || !college.trim()) {
        showMessage('Please fill in all required fields', 'error');
        return;
    }

    const saveBtn = document.getElementById('save-profile-btn');
    saveBtn.disabled = true;
    saveBtn.innerHTML = '<i class="fas fa-spinner fa-spin"></i> Saving...';

    try {
        // Get image data if any
        const imagePreview = document.getElementById('edit-image-preview');
        const imageData = imagePreview ? imagePreview.dataset.imageData : null;

        const requestBody = {
            full_name: fullName,
            email: email,
            college: college,
            bio: bio,
            skills: skills,
            phone: phone,
            location: location,
            title: title,
            twitter: twitter,
            linkedin: linkedin,
            github: github
        };

        // Include image data if available
        if (imageData) {
            if (imageData === 'REMOVE') {
                requestBody.profile_image = null;
            } else {
                requestBody.profile_image = imageData;
            }
        }

        const response = await fetch('/api/profile', {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(requestBody)
        });

        if (response.ok) {
            const data = await response.json();
            currentUser = data.user;
            updateProfileInfo();
            showMessage('Profile updated successfully!', 'success');
            closeModals();
        } else {
            const errorData = await response.json();
            showMessage(errorData.error || 'Error updating profile', 'error');
        }

    } catch (error) {
        console.error('Error updating profile:', error);
        showMessage('Error updating profile', 'error');
    } finally {
        saveBtn.disabled = false;
        saveBtn.innerHTML = '<i class="fas fa-save"></i> Save Changes';
    }
}

function closeModals() {
    const modals = document.querySelectorAll('.modal');
    modals.forEach(modal => modal.classList.remove('show'));
}

// Function to handle clicking on user names in comments and collaborations
function handleUserNameClick(userId, username) {
    if (userId && userId !== currentUser?.id) {
        window.location.href = `/profile.html?user_id=${userId}`;
    }
}

// Make user names clickable in generated content
function makeUserNamesClickable(container) {
    const userElements = container.querySelectorAll('[data-user-id]');
    userElements.forEach(element => {
        const userId = element.dataset.userId;
        const username = element.textContent;
        if (userId && userId !== currentUser?.id) {
            element.style.cursor = 'pointer';
            element.style.color = '#28a745';
            element.style.textDecoration = 'underline';
            element.addEventListener('click', () => handleUserNameClick(userId, username));
        }
    });
}

// Utility functions
function escapeHtml(text) {
    if (!text) return '';
    const map = {
        '&': '&amp;',
        '<': '&lt;',
        '>': '&gt;',
        '"': '&quot;',
        "'": '&#039;'
    };
    return text.replace(/[&<>"']/g, function(m) { return map[m]; });
}

function formatDate(dateString) {
    try {
        const date = new Date(dateString);
        // Always return only the date part, no time
        return date.toLocaleDateString('en-US', {
            year: 'numeric',
            month: 'short',
            day: 'numeric'
        });
    } catch (error) {
        return 'Invalid date';
    }
}

function showMessage(message, type) {
    // Remove popup messages - just log to console instead
    console.log(`${type}: ${message}`);
}

// Handle logout
async function handleLogout() {
    try {
        const response = await fetch('/api/logout', { method: 'POST' });
        if (response.ok) {
            window.location.href = '/';
        }
    } catch (error) {
        console.error('Logout error:', error);
    }
}

// Real-time updates for timestamps
function startRealTimeUpdates() {
    // Update all timestamps every minute
    setInterval(updateAllTimestamps, 60000);
}

function updateAllTimestamps() {
    // Update project timestamps
    document.querySelectorAll('[data-timestamp]').forEach(element => {
        const timestamp = element.getAttribute('data-timestamp');
        if (timestamp) {
            element.textContent = formatDate(timestamp);
        }
    });

    // Update activity timestamps
    document.querySelectorAll('.activity-time[data-timestamp]').forEach(element => {
        const timestamp = element.getAttribute('data-timestamp');
        if (timestamp) {
            element.textContent = formatDate(timestamp);
        }
    });

    // Update list item timestamps
    document.querySelectorAll('.list-item-date[data-timestamp]').forEach(element => {
        const timestamp = element.getAttribute('data-timestamp');
        if (timestamp) {
            element.textContent = formatDate(timestamp);
        }
    });
}

// Search functionality
function setupSearchFunction() {
    const searchBtn = document.querySelector('.search-btn');
    const searchInput = document.getElementById('search-input');

    if (searchBtn) {
        searchBtn.addEventListener('click', performSearch);
    }

    if (searchInput) {
        // Allow Enter key to trigger search
        searchInput.addEventListener('keypress', function(e) {
            if (e.key === 'Enter') {
                performSearch();
            }
        });
    }
}

function handleSearch() {
    const searchQuery = prompt('Enter search term:');
    if (!searchQuery || searchQuery.trim() === '') return;

    const activeTab = document.querySelector('.tab-btn.active').getAttribute('data-tab');
    performSearch(searchQuery.trim(), activeTab);
}

function performSearch() {
    const searchInput = document.getElementById('search-input');

    if (!searchInput) return;

    const searchTerm = searchInput.value.trim();

    if (!searchTerm) {
        alert('Please enter a search term');
        return;
    }

    // Show search notification
    showSearchNotification(searchTerm, 'all');

    // Perform search across all sections
    searchAllSections(searchTerm.toLowerCase());

    // Clear the search input
    searchInput.value = '';
}

function loadAllSections() {
    // Load all sections at once since we no longer have tabs
    loadUserProjects();
    loadUserCollaborations();
    loadUserDonations();
    loadUserActivity();
}

function searchAllSections(searchTerm) {
    // Search across all sections
    searchProjects(searchTerm);
    searchCollaborations(searchTerm);
    searchDonations(searchTerm);
    searchActivity(searchTerm);
}

function showSearchNotification(searchTerm, section) {
    // Remove existing notifications
    const existingNotification = document.querySelector('.search-notification');
    if (existingNotification) {
        existingNotification.remove();
    }

    // Create new notification
    const notification = document.createElement('div');
    notification.className = 'search-notification';
    notification.innerHTML = `
        <span>Searching for "${searchTerm}" across all sections</span>
        <button class="clear-search-btn" onclick="clearSearch()">Clear</button>
    `;

    // Insert after search section
    const searchSection = document.querySelector('.search-section');
    if (searchSection) {
        searchSection.parentNode.insertBefore(notification, searchSection.nextSibling);
    }
}

function clearSearch() {
    // Remove search notification
    const notification = document.querySelector('.search-notification');
    if (notification) {
        notification.remove();
    }

    // Reload all sections to show all content
    loadAllSections();
}

function searchProjects(searchTerm) {
    const projectCards = document.querySelectorAll('.project-card');
    let visibleCount = 0;

    projectCards.forEach(card => {
        const title = card.querySelector('.project-title').textContent.toLowerCase();
        const description = card.querySelector('.project-description').textContent.toLowerCase();
        const techLabel = card.querySelector('.tech-label');
        const tech = techLabel ? techLabel.textContent.toLowerCase() : '';

        if (title.includes(searchTerm) || description.includes(searchTerm) || tech.includes(searchTerm)) {
            card.style.display = 'block';
            visibleCount++;
        } else {
            card.style.display = 'none';
        }
    });

    showSearchResults(visibleCount, searchTerm);
}

function searchCollaborations(searchTerm) {
    const collaborationCards = document.querySelectorAll('.collaboration-card');
    let visibleCount = 0;

    collaborationCards.forEach(card => {
        const title = card.querySelector('h3').textContent.toLowerCase();
        const role = card.textContent.toLowerCase();

        if (title.includes(searchTerm) || role.includes(searchTerm)) {
            card.style.display = 'block';
            visibleCount++;
        } else {
            card.style.display = 'none';
        }
    });

    showSearchResults(visibleCount, searchTerm);
}

function searchDonations(searchTerm) {
    const donationCards = document.querySelectorAll('.donation-card');
    let visibleCount = 0;

    donationCards.forEach(card => {
        const title = card.querySelector('h3').textContent.toLowerCase();

        if (title.includes(searchTerm)) {
            card.style.display = 'block';
            visibleCount++;
        } else {
            card.style.display = 'none';
        }
    });

    showSearchResults(visibleCount, searchTerm);
}

function searchActivity(searchTerm) {
    const activityItems = document.querySelectorAll('.activity-item');
    let visibleCount = 0;

    activityItems.forEach(item => {
        const description = item.querySelector('p').textContent.toLowerCase();

        if (description.includes(searchTerm)) {
            item.style.display = 'flex';
            visibleCount++;
        } else {
            item.style.display = 'none';
        }
    });

    showSearchResults(visibleCount, searchTerm);
}

function showSearchResults(count, searchTerm) {
    // Show search results notification
    const existingNotification = document.querySelector('.search-notification');
    if (existingNotification) {
        existingNotification.remove();
    }

    const notification = document.createElement('div');
    notification.className = 'search-notification';
    notification.innerHTML = `
        <span>Found ${count} results for "${searchTerm}"</span>
        <button onclick="clearSearch()" class="clear-search-btn">Clear</button>
    `;

    const contentHeader = document.querySelector('.content-header');
    contentHeader.appendChild(notification);

    // Auto-remove after 5 seconds
    setTimeout(() => {
        if (notification.parentNode) {
            notification.remove();
        }
    }, 5000);
}

function clearSearch() {
    // Show all items again
    const allCards = document.querySelectorAll('.project-card, .collaboration-card, .donation-card, .activity-item');
    allCards.forEach(card => {
        card.style.display = '';
    });

    // Remove notification
    const notification = document.querySelector('.search-notification');
    if (notification) {
        notification.remove();
    }
}

static/user-profile.css.content:
/* User Profile Page Dark Theme Styles */

/* Base Dark Theme Override */
body {
    background-color: #1a1a2e !important;
    color: #ffffff !important;
    font-family: 'Segoe UI', sans-serif !important;
    margin: 0 !important;
    padding: 0 !important;
}

/* Dark navbar override */
.navbar {
    background-color: #1a1a2e !important;
    border-bottom: 1px solid #333 !important;
}

/* Main Container */
.user-profile-container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 2rem 20px;
    min-height: 100vh;
}

/* Loading State */
.loading-container {
    display: flex;
    justify-content: center;
    align-items: center;
    min-height: 60vh;
}

.loading-spinner {
    text-align: center;
    color: rgba(255, 255, 255, 0.7);
}

.loading-spinner i {
    font-size: 3rem;
    color: #ffd700;
    margin-bottom: 1rem;
}

.loading-spinner p {
    font-size: 1.1rem;
}

/* Error State */
.error-container {
    display: flex;
    justify-content: center;
    align-items: center;
    min-height: 60vh;
}

.error-message {
    text-align: center;
    background: rgba(255, 255, 255, 0.05);
    backdrop-filter: blur(12px);
    border-radius: 16px;
    padding: 3rem;
    border: 1px solid rgba(255, 255, 255, 0.1);
}

.error-message i {
    font-size: 4rem;
    color: #ff6b6b;
    margin-bottom: 1rem;
}

.error-message h2 {
    color: white;
    margin-bottom: 1rem;
}

.error-message p {
    color: rgba(255, 255, 255, 0.7);
    margin-bottom: 2rem;
}

/* Profile Header */
.profile-header {
    background: rgba(255, 255, 255, 0.05);
    backdrop-filter: blur(12px);
    border-radius: 20px;
    padding: 2.5rem;
    margin-bottom: 2rem;
    border: 1px solid rgba(255, 255, 255, 0.1);
    display: flex;
    gap: 2rem;
    align-items: center;
}

.profile-avatar {
    flex-shrink: 0;
}

.profile-avatar i {
    font-size: 8rem;
    color: #ffd700;
    background: rgba(255, 215, 0, 0.1);
    border-radius: 50%;
    padding: 1rem;
}

.profile-info {
    flex: 1;
}

.user-name {
    font-size: 2.5rem;
    font-weight: 700;
    color: white;
    margin-bottom: 0.5rem;
}

.user-college {
    font-size: 1.2rem;
    color: rgba(255, 255, 255, 0.8);
    margin-bottom: 0.5rem;
}

.user-username {
    font-size: 1rem;
    color: #ffd700;
    margin-bottom: 1.5rem;
}

.user-stats {
    display: flex;
    gap: 2rem;
}

.stat {
    text-align: center;
}

.stat-number {
    display: block;
    font-size: 1.8rem;
    font-weight: bold;
    color: #ffd700;
}

.stat-label {
    font-size: 0.9rem;
    color: rgba(255, 255, 255, 0.6);
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

/* Profile Actions */
.profile-actions {
    display: flex;
    flex-direction: column;
    gap: 1rem;
    flex-shrink: 0;
}

.action-btn {
    background: linear-gradient(135deg, #ffd700, #ffed4e);
    color: #1a1a2e;
    border: none;
    border-radius: 25px;
    padding: 12px 24px;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
    display: flex;
    align-items: center;
    gap: 8px;
    min-width: 160px;
    justify-content: center;
}

.action-btn:hover {
    transform: translateY(-2px);
    box-shadow: 0 6px 20px rgba(255, 215, 0, 0.3);
}

.action-btn.secondary {
    background: rgba(255, 255, 255, 0.1);
    color: rgba(255, 255, 255, 0.8);
    border: 1px solid rgba(255, 255, 255, 0.2);
}

.action-btn.secondary:hover {
    background: rgba(255, 255, 255, 0.15);
    color: white;
    box-shadow: 0 6px 20px rgba(255, 255, 255, 0.1);
}

.action-btn.pending {
    background: rgba(255, 152, 0, 0.2);
    color: #ff9800;
    border: 1px solid #ff9800;
    cursor: not-allowed;
}

.action-btn.following {
    background: rgba(76, 175, 80, 0.2);
    color: #4caf50;
    border: 1px solid #4caf50;
}

/* Profile Tabs */
.profile-tabs {
    display: flex;
    gap: 8px;
    margin-bottom: 2rem;
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}

.tab-btn {
    background: none;
    border: none;
    color: rgba(255, 255, 255, 0.7);
    padding: 1rem 1.5rem;
    cursor: pointer;
    border-radius: 8px 8px 0 0;
    transition: all 0.3s ease;
    font-size: 1rem;
    font-weight: 500;
    display: flex;
    align-items: center;
    gap: 8px;
}

.tab-btn:hover {
    color: white;
    background: rgba(255, 255, 255, 0.05);
}

.tab-btn.active {
    color: #ffd700;
    background: rgba(255, 215, 0, 0.1);
    border-bottom: 2px solid #ffd700;
}

/* Tab Content */
.tab-content {
    min-height: 400px;
}

.tab-pane {
    display: none;
}

.tab-pane.active {
    display: block;
}

/* Projects Grid */
.projects-grid {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(350px, 1fr));
    gap: 1.5rem;
}

.project-card {
    background: rgba(255, 255, 255, 0.05);
    backdrop-filter: blur(12px);
    border-radius: 16px;
    padding: 1.5rem;
    border: 1px solid rgba(255, 255, 255, 0.1);
    transition: all 0.3s ease;
    cursor: pointer;
}

.project-card:hover {
    background: rgba(255, 255, 255, 0.08);
    transform: translateY(-2px);
    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.3);
}

.project-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 1rem;
}

.project-category {
    background: linear-gradient(135deg, #ffd700, #ffed4e);
    color: #1a1a2e;
    padding: 4px 12px;
    border-radius: 12px;
    font-size: 0.8rem;
    font-weight: 600;
}

.project-status {
    padding: 4px 12px;
    border-radius: 12px;
    font-size: 0.8rem;
    font-weight: 600;
    text-transform: uppercase;
}

.project-status.active {
    background: rgba(76, 175, 80, 0.2);
    color: #4caf50;
}

.project-title {
    color: white;
    font-size: 1.3rem;
    font-weight: 600;
    margin-bottom: 0.5rem;
}

.project-description {
    color: rgba(255, 255, 255, 0.8);
    line-height: 1.6;
    margin-bottom: 1rem;
}

.project-stats {
    display: flex;
    gap: 1rem;
    align-items: center;
}

.project-stat {
    display: flex;
    align-items: center;
    gap: 4px;
    color: rgba(255, 255, 255, 0.6);
    font-size: 0.9rem;
}

.project-stat i {
    color: #ffd700;
}

/* About Section */
.about-section {
    background: rgba(255, 255, 255, 0.05);
    backdrop-filter: blur(12px);
    border-radius: 16px;
    padding: 2rem;
    border: 1px solid rgba(255, 255, 255, 0.1);
}

.about-section h3 {
    color: white;
    font-size: 1.3rem;
    margin-bottom: 1rem;
    display: flex;
    align-items: center;
    gap: 8px;
}

.about-section h3 i {
    color: #ffd700;
}

.about-section p {
    color: rgba(255, 255, 255, 0.8);
    line-height: 1.6;
    margin-bottom: 2rem;
}

.stats-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 1rem;
}

.stat-card {
    background: rgba(255, 255, 255, 0.03);
    border-radius: 12px;
    padding: 1.5rem;
    display: flex;
    align-items: center;
    gap: 1rem;
    border: 1px solid rgba(255, 255, 255, 0.1);
}

.stat-card i {
    font-size: 2rem;
    color: #ffd700;
}

.stat-card .stat-info h4 {
    color: white;
    font-size: 1.5rem;
    font-weight: 700;
    margin-bottom: 0.25rem;
}

.stat-card .stat-info p {
    color: rgba(255, 255, 255, 0.6);
    font-size: 0.9rem;
    margin: 0;
}

/* Modal Styles */
.modal {
    display: none;
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.8);
    backdrop-filter: blur(8px);
    z-index: 1000;
    justify-content: center;
    align-items: center;
}

.modal.show {
    display: flex;
}

.modal-content {
    background: #1a1a2e;
    backdrop-filter: blur(12px);
    border-radius: 16px;
    width: 90%;
    max-width: 500px;
    border: 1px solid rgba(255, 255, 255, 0.2);
}

.modal-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 1.5rem 2rem;
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}

.modal-header h3 {
    color: white;
    font-size: 1.3rem;
    font-weight: 600;
    margin: 0;
}

.close-btn {
    background: none;
    border: none;
    color: rgba(255, 255, 255, 0.7);
    cursor: pointer;
    font-size: 1.2rem;
    padding: 4px;
    border-radius: 50%;
    transition: all 0.3s ease;
}

.close-btn:hover {
    background: rgba(255, 255, 255, 0.1);
    color: white;
}

.modal-body {
    padding: 2rem;
}

.modal-body p {
    color: rgba(255, 255, 255, 0.8);
    margin-bottom: 1.5rem;
}

.form-group {
    margin-bottom: 1.5rem;
}

.form-group label {
    display: block;
    color: white;
    font-weight: 500;
    margin-bottom: 0.5rem;
}

.form-group select,
.form-group textarea {
    width: 100%;
    background: rgba(255, 255, 255, 0.1);
    border: 1px solid rgba(255, 255, 255, 0.2);
    border-radius: 8px;
    padding: 12px 16px;
    color: white;
    font-size: 1rem;
    font-family: inherit;
    resize: vertical;
}

.form-group select:focus,
.form-group textarea:focus {
    outline: none;
    border-color: #ffd700;
    background: rgba(255, 255, 255, 0.15);
}

.form-group textarea {
    min-height: 100px;
}

.modal-actions {
    display: flex;
    gap: 1rem;
    justify-content: flex-end;
    padding: 1.5rem 2rem;
    border-top: 1px solid rgba(255, 255, 255, 0.1);
}

.btn-secondary,
.btn-primary {
    padding: 10px 20px;
    border-radius: 8px;
    font-size: 0.9rem;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
    border: none;
}

.btn-secondary {
    background: rgba(255, 255, 255, 0.1);
    color: rgba(255, 255, 255, 0.8);
    border: 1px solid rgba(255, 255, 255, 0.2);
}

.btn-secondary:hover {
    background: rgba(255, 255, 255, 0.15);
    color: white;
}

.btn-primary {
    background: linear-gradient(135deg, #ffd700, #ffed4e);
    color: #1a1a2e;
}

.btn-primary:hover {
    transform: translateY(-1px);
    box-shadow: 0 4px 12px rgba(255, 215, 0, 0.3);
}

/* Message Container */
.message-container {
    position: fixed;
    top: 20px;
    right: 20px;
    z-index: 1100;
    display: flex;
    flex-direction: column;
    gap: 10px;
}

.message {
    background: #1a1a2e;
    backdrop-filter: blur(12px);
    border-radius: 8px;
    padding: 12px 20px;
    color: white;
    font-weight: 500;
    border-left: 4px solid;
    animation: slideInFromRight 0.3s ease-out;
    max-width: 300px;
    border: 1px solid rgba(255, 255, 255, 0.1);
}

.message.success {
    border-left-color: #4caf50;
    background: rgba(76, 175, 80, 0.1);
}

.message.error {
    border-left-color: #f44336;
    background: rgba(244, 67, 54, 0.1);
}

.message.info {
    border-left-color: #2196f3;
    background: rgba(33, 150, 243, 0.1);
}

@keyframes slideInFromRight {
    from {
        transform: translateX(100%);
        opacity: 0;
    }
    to {
        transform: translateX(0);
        opacity: 1;
    }
}

/* Responsive Design */
@media (max-width: 768px) {
    .profile-header {
        flex-direction: column;
        text-align: center;
        padding: 2rem;
    }

    .profile-actions {
        flex-direction: row;
        justify-content: center;
        width: 100%;
    }

    .user-stats {
        justify-content: center;
    }

    .projects-grid {
        grid-template-columns: 1fr;
    }

    .stats-grid {
        grid-template-columns: 1fr;
    }
}

/* Empty State */
.empty-state {
    text-align: center;
    padding: 3rem;
    color: rgba(255, 255, 255, 0.6);
}

.empty-state i {
    font-size: 4rem;
    color: rgba(255, 255, 255, 0.3);
    margin-bottom: 1rem;
}

.empty-state h3 {
    color: white;
    font-size: 1.5rem;
    margin-bottom: 0.5rem;
}

.empty-state p {
    font-size: 1rem;
}

static/users.css.content:
/* Users Discovery Page Dark Theme Styles - Consistent with Discussion Page */

/* Base Dark Theme Override - Match Discussion Page */
body {
    background-color: #0d0d0d !important;
    color: #ffffff !important;
    font-family: 'Segoe UI', sans-serif !important;
    margin: 0 !important;
    padding: 0 !important;
}

/* Dark navbar override - Match Discussion Page */
.navbar {
    background-color: #0d0d0d !important;
    background: #0d0d0d !important;
    border-bottom: 1px solid #333 !important;
}

/* Ensure all scrollable areas maintain dark background */
html, body, * {
    scrollbar-color: #333 #0d0d0d;
}

*::-webkit-scrollbar {
    width: 8px;
    background-color: #0d0d0d;
}

*::-webkit-scrollbar-track {
    background-color: #0d0d0d;
}

*::-webkit-scrollbar-thumb {
    background-color: #333;
    border-radius: 4px;
}

*::-webkit-scrollbar-thumb:hover {
    background-color: #555;
}

/* Active navigation state */
.nav-links a.active {
    color: #ffd700;
    border-bottom: 2px solid #ffd700;
}

/* User Profile Dropdown - Match Discussion Page */
.user-profile {
    position: relative;
}

.profile-btn {
    background: rgba(255, 255, 255, 0.1);
    border: 1px solid rgba(255, 255, 255, 0.2);
    border-radius: 25px;
    color: white;
    padding: 8px 16px;
    cursor: pointer;
    display: flex;
    align-items: center;
    gap: 8px;
    font-size: 0.9rem;
    transition: all 0.3s ease;
}

.profile-btn:hover {
    background: rgba(255, 255, 255, 0.15);
    border-color: #ffd700;
}

.dropdown-menu {
    position: absolute;
    top: 100%;
    right: 0;
    background: #0d0d0d;
    backdrop-filter: blur(12px);
    border-radius: 8px;
    border: 1px solid rgba(255, 255, 255, 0.2);
    padding: 8px 0;
    min-width: 160px;
    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.5);
    z-index: 1000;
    display: none;
    margin-top: 8px;
}

.dropdown-item {
    display: flex;
    align-items: center;
    gap: 8px;
    padding: 10px 16px;
    color: rgba(255, 255, 255, 0.8);
    text-decoration: none;
    font-size: 0.9rem;
    transition: all 0.3s ease;
}

.dropdown-item:hover {
    background: rgba(255, 255, 255, 0.1);
    color: white;
}

.dropdown-divider {
    height: 1px;
    background: rgba(255, 255, 255, 0.1);
    margin: 4px 0;
}

/* Main Container */
.users-container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 2rem 20px;
    min-height: 100vh;
}

/* Page Header */
.page-header {
    text-align: center;
    margin-bottom: 3rem;
}

.page-header h1 {
    font-size: 3rem;
    font-weight: 700;
    color: white;
    margin-bottom: 1rem;
}

.page-header h1 i {
    color: #ffd700;
    margin-right: 1rem;
}

.page-header p {
    font-size: 1.2rem;
    color: rgba(255, 255, 255, 0.7);
    max-width: 600px;
    margin: 0 auto;
}

/* Search Section */
.search-section {
    background: rgba(255, 255, 255, 0.05);
    backdrop-filter: blur(12px);
    border-radius: 20px;
    padding: 2rem;
    margin-bottom: 2rem;
    border: 1px solid rgba(255, 255, 255, 0.1);
}

.search-bar {
    display: flex;
    gap: 1rem;
    align-items: center;
    margin-bottom: 1rem;
}

.search-input-container {
    flex: 1;
    position: relative;
    display: flex;
    align-items: center;
}

.search-input-container i {
    position: absolute;
    left: 16px;
    color: rgba(255, 255, 255, 0.5);
    font-size: 1.1rem;
}

#search-input {
    width: 100%;
    background: rgba(255, 255, 255, 0.1);
    border: 1px solid rgba(255, 255, 255, 0.2);
    border-radius: 25px;
    padding: 16px 50px 16px 50px;
    color: white;
    font-size: 1rem;
    transition: all 0.3s ease;
}

#search-input::placeholder {
    color: rgba(255, 255, 255, 0.5);
}

#search-input:focus {
    outline: none;
    border-color: #ffd700;
    background: rgba(255, 255, 255, 0.15);
    box-shadow: 0 0 0 3px rgba(255, 215, 0, 0.1);
}

.clear-search {
    position: absolute;
    right: 16px;
    background: none;
    border: none;
    color: rgba(255, 255, 255, 0.5);
    cursor: pointer;
    padding: 4px;
    border-radius: 50%;
    transition: all 0.3s ease;
    display: none;
}

.clear-search:hover {
    background: rgba(255, 255, 255, 0.1);
    color: white;
}

.clear-search.visible {
    display: block;
}

.search-btn {
    background: linear-gradient(135deg, #ffd700, #ffed4e);
    color: #1a1a2e;
    border: none;
    border-radius: 25px;
    padding: 16px 24px;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
    white-space: nowrap;
}

.search-btn:hover {
    transform: translateY(-2px);
    box-shadow: 0 6px 20px rgba(255, 215, 0, 0.3);
}

.search-stats {
    text-align: center;
    color: rgba(255, 255, 255, 0.7);
    font-size: 1rem;
}

.search-stats span {
    color: #ffd700;
    font-weight: 600;
}

/* Users Grid */
.users-grid {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(320px, 1fr));
    gap: 1.5rem;
    margin-bottom: 2rem;
}

.user-card {
    background: rgba(255, 255, 255, 0.05);
    backdrop-filter: blur(12px);
    border-radius: 20px;
    padding: 2rem;
    border: 1px solid rgba(255, 255, 255, 0.1);
    transition: all 0.3s ease;
    cursor: pointer;
    position: relative;
    overflow: hidden;
}

.user-card:hover {
    background: rgba(255, 255, 255, 0.08);
    transform: translateY(-5px);
    box-shadow: 0 12px 40px rgba(0, 0, 0, 0.3);
}

.user-card::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 4px;
    background: linear-gradient(135deg, #ffd700, #ffed4e);
    opacity: 0;
    transition: opacity 0.3s ease;
}

.user-card:hover::before {
    opacity: 1;
}

.user-header {
    display: flex;
    align-items: center;
    gap: 1rem;
    margin-bottom: 1.5rem;
}

.user-avatar {
    flex-shrink: 0;
}

.user-avatar i {
    font-size: 3.5rem;
    color: #ffd700;
    background: rgba(255, 215, 0, 0.1);
    border-radius: 50%;
    padding: 0.8rem;
}

.user-info h3 {
    color: white;
    font-size: 1.3rem;
    font-weight: 600;
    margin-bottom: 0.25rem;
}

.user-info .username {
    color: #ffd700;
    font-size: 0.9rem;
    margin-bottom: 0.5rem;
}

.user-info .college {
    color: rgba(255, 255, 255, 0.7);
    font-size: 0.9rem;
}

.user-stats {
    display: flex;
    justify-content: space-around;
    margin-bottom: 1.5rem;
    padding: 1rem 0;
    border-top: 1px solid rgba(255, 255, 255, 0.1);
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}

.user-stat {
    text-align: center;
}

.user-stat .stat-number {
    display: block;
    font-size: 1.4rem;
    font-weight: bold;
    color: #ffd700;
    margin-bottom: 0.25rem;
}

.user-stat .stat-label {
    font-size: 0.8rem;
    color: rgba(255, 255, 255, 0.6);
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.user-actions {
    display: flex;
    gap: 0.75rem;
}

.action-btn {
    flex: 1;
    background: linear-gradient(135deg, #ffd700, #ffed4e);
    color: #1a1a2e;
    border: none;
    border-radius: 20px;
    padding: 8px 16px;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
    font-size: 0.9rem;
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 6px;
}

.action-btn:hover {
    transform: translateY(-1px);
    box-shadow: 0 4px 12px rgba(255, 215, 0, 0.3);
}

.action-btn.secondary {
    background: rgba(255, 255, 255, 0.1);
    color: rgba(255, 255, 255, 0.8);
    border: 1px solid rgba(255, 255, 255, 0.2);
}

.action-btn.secondary:hover {
    background: rgba(255, 255, 255, 0.15);
    color: white;
    box-shadow: 0 4px 12px rgba(255, 255, 255, 0.1);
}

.action-btn.login-required {
    background: rgba(255, 152, 0, 0.2);
    color: #ff9800;
    border: 1px solid #ff9800;
    cursor: not-allowed;
}

/* Loading State */
.loading-container {
    display: flex;
    justify-content: center;
    align-items: center;
    min-height: 60vh;
}

.loading-spinner {
    text-align: center;
    color: rgba(255, 255, 255, 0.7);
}

.loading-spinner i {
    font-size: 3rem;
    color: #ffd700;
    margin-bottom: 1rem;
}

.loading-spinner p {
    font-size: 1.1rem;
}

/* Empty State */
.empty-container {
    display: flex;
    justify-content: center;
    align-items: center;
    min-height: 60vh;
}

.empty-state {
    text-align: center;
    background: rgba(255, 255, 255, 0.05);
    backdrop-filter: blur(12px);
    border-radius: 20px;
    padding: 3rem;
    border: 1px solid rgba(255, 255, 255, 0.1);
}

.empty-state i {
    font-size: 4rem;
    color: rgba(255, 255, 255, 0.3);
    margin-bottom: 1rem;
}

.empty-state h3 {
    color: white;
    font-size: 1.5rem;
    margin-bottom: 0.5rem;
}

.empty-state p {
    color: rgba(255, 255, 255, 0.7);
    font-size: 1rem;
}

/* Pagination */
.pagination {
    display: flex;
    justify-content: center;
    align-items: center;
    gap: 1rem;
    margin-top: 2rem;
}

.page-btn {
    background: rgba(255, 255, 255, 0.1);
    color: rgba(255, 255, 255, 0.8);
    border: 1px solid rgba(255, 255, 255, 0.2);
    border-radius: 8px;
    padding: 10px 16px;
    cursor: pointer;
    transition: all 0.3s ease;
    font-weight: 500;
}

.page-btn:hover:not(:disabled) {
    background: rgba(255, 255, 255, 0.15);
    color: white;
    border-color: #ffd700;
}

.page-btn:disabled {
    opacity: 0.5;
    cursor: not-allowed;
}

.page-info {
    color: rgba(255, 255, 255, 0.8);
    font-weight: 500;
    padding: 0 1rem;
}

/* Guest Notice */
.guest-notice {
    position: fixed;
    bottom: 20px;
    left: 50%;
    transform: translateX(-50%);
    z-index: 1000;
    animation: slideUpFromBottom 0.3s ease-out;
}

.notice-content {
    background: linear-gradient(135deg, rgba(255, 215, 0, 0.15), rgba(255, 215, 0, 0.1));
    backdrop-filter: blur(12px);
    border: 1px solid rgba(255, 215, 0, 0.3);
    border-radius: 12px;
    padding: 16px 24px;
    color: white;
    display: flex;
    align-items: center;
    gap: 12px;
    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.3);
}

.notice-content i {
    color: #ffd700;
    font-size: 1.2rem;
    flex-shrink: 0;
}

.notice-content a {
    color: #ffd700;
    text-decoration: none;
    font-weight: 600;
}

.notice-content a:hover {
    text-decoration: underline;
}

@keyframes slideUpFromBottom {
    from {
        transform: translateX(-50%) translateY(100%);
        opacity: 0;
    }
    to {
        transform: translateX(-50%) translateY(0);
        opacity: 1;
    }
}

/* Message Container */
.message-container {
    position: fixed;
    top: 20px;
    right: 20px;
    z-index: 1100;
    display: flex;
    flex-direction: column;
    gap: 10px;
}

.message {
    background: #1a1a2e;
    backdrop-filter: blur(12px);
    border-radius: 8px;
    padding: 12px 20px;
    color: white;
    font-weight: 500;
    border-left: 4px solid;
    animation: slideInFromRight 0.3s ease-out;
    max-width: 300px;
    border: 1px solid rgba(255, 255, 255, 0.1);
}

.message.success {
    border-left-color: #4caf50;
    background: rgba(76, 175, 80, 0.1);
}

.message.error {
    border-left-color: #f44336;
    background: rgba(244, 67, 54, 0.1);
}

.message.info {
    border-left-color: #2196f3;
    background: rgba(33, 150, 243, 0.1);
}

@keyframes slideInFromRight {
    from {
        transform: translateX(100%);
        opacity: 0;
    }
    to {
        transform: translateX(0);
        opacity: 1;
    }
}

/* Responsive Design */
@media (max-width: 768px) {
    .users-container {
        padding: 1rem 15px;
    }

    .page-header h1 {
        font-size: 2.5rem;
    }

    .search-bar {
        flex-direction: column;
        gap: 1rem;
    }

    .search-btn {
        width: 100%;
    }

    .users-grid {
        grid-template-columns: 1fr;
        gap: 1rem;
    }

    .user-card {
        padding: 1.5rem;
    }

    .user-actions {
        flex-direction: column;
    }

    .pagination {
        flex-direction: column;
        gap: 0.5rem;
    }

    .guest-notice {
        left: 10px;
        right: 10px;
        transform: none;
    }
}

@media (max-width: 480px) {
    .user-header {
        flex-direction: column;
        text-align: center;
        gap: 1rem;
    }

    .user-stats {
        justify-content: space-around;
    }
}

